using System;class S{static void Main(){int i,n,s,t=int.Parse(Console.ReadLine());while(t>0){n=int.Parse(Console.ReadLine());int[]v=Array.ConvertAll(Console.ReadLine().Split(),int.Parse);int[]m=Array.ConvertAll(Console.ReadLine().Split(),int.Parse);s=0;for (i=1;i<n;i++){ if (m[i]+m[i-1]>v[i]){s+=m[i]+m[i-1]-v[i];m[i]=v[i];}else m[i]=m[i]+m[i-1];}Console.WriteLine(m[n-1]+" "+s);t--;}}}